  }

  private static final int[][] grid1 = {
          {1, 7, 2, 9, 8, 3, 5, 6, 4},
          {4, 6, 8, 5, 7, 2, 9, 8, 3},
          {5, 3, 9, 6, 1, 4, 8, 7, 2},
          {2, 1, 3, 8, 5, 6, 4, 9, 7},
          {8, 4, 6, 3, 9, 7, 2, 5, 1},
          {9, 5, 7, 2, 4, 1, 3, 1, 6},
          {6, 8, 4, 7, 2, 5, 1, 3, 9},
          {3, 9, 1, 4, 6, 8, 7, 2, 5},
          {7, 2, 5, 1, 3, 9, 6, 4, 8},

  };

  private static final int[][] grid2 = {
          {1, 7, 2, 9, 8, 3, 5, 6, 4},
          {4, 6, 8, 5, 7, 2, 9, 1, 3},
          {5, 3, 9, 6, 1, 4, 8, 7, 2},
          {2, 1, 3, 8, 5, 6, 4, 9, 7},
          {8, 4, 6, 3, 9, 7, 2, 5, 1},
          {9, 5, 7, 2, 4, 1, 3, 8, 6},
          {6, 8, 4, 7, 2, 5, 1, 3, 9},
          {3, 9, 1, 4, 6, 8, 5, 2, 7},
          {7, 2, 5, 1, 3, 9, 6, 4, 8},
  };

  private static final int[][] grid3 = {
          {1, 7, 2, 9, 8, 3, 5, 6, 4},
          {4, 6, 8, 5, 7, 2, 9, 1, 3},
          {5, 3, 9, 6, 1, 4, 8, 7, 2},
          {7, 2, 5, 1, 3, 9, 6, 4, 8},
          {2, 1, 3, 8, 5, 6, 4, 9, 7},
          {8, 4, 6, 3, 9, 7, 2, 5, 1},
