public class Tests {
  protected void testPoint(int x, int y) {
    Point p = new Point(x, y);
    Assert.assertEquals("Incorrect x-coordinate.", x, p.getX());
    Assert.assertEquals("Incorrect y-coordinate.", y, p.getY());
  }

