int x;
    int y;
    int width;
    int height;

int x;
    int y;
    int width;
    int height;

public class Spacecraft {
    int x;
    int y;
    int width;
    int height;

    /* write your code here */

    /* write your code here */

    public Spacecraft(int width, int height) {
        /* write your code here */
        this.width = width;
        this.height = height;
    }

    /* write your code here */

    /* write your code here */

    public void setPosition(int x, int y) {
        /* write your code here */
        pos = new Point(x, y);
    }

    /* write your code here */

    private Point pos = new Point(0, 0);;

    public Spacecraft() {
        width = height = 1;
    }

    public Spacecraft(int side) {
        width = height = side;
    }

    public Point position() { return pos; }
    public int width() { return width; }
    public int height() { return height; }

    public int area() {
        return width * height;
    }

    public int perimeter() {
        return (width + height) * 2;
    }

    public void setSize(int side) {
        width = height = side;
    }

    public void setSize(int width, int height) {
        this.width = width;
        this.height = height;
    }
}

